'            ora_hash(f.VIEW_TIMESTAMP,29) icon_modifier,',
'            --',
'            upper(',
'                  decode(instr(replace(f.APP_USERNAME,''.'','' ''),'' ''),',
'                         0, ',
'                         substr(f.APP_USERNAME,1,2),',
'                         substr(f.APP_USERNAME,1,1)||substr(f.APP_USERNAME,instr(replace(f.APP_USERNAME,''.'','' ''),'' '')+1,1)',
