'                            and r.region_id = i.region_id',
'                            and r.application_id = p_app_id',
'                            and r.page_id = p_page_id',
'                            and r.static_id = p_irr_region_static_id ) loop',
'                l_interactive_report_id := c1.interactive_report_id;',
